package lib

import (
	"fmt"
	"os"
	"sort"
	"sync"
	"time"

	"log/slog"
)

type Zone struct {
	*time.Location

	CountryCode string
	Zone        string
	Abbr        []string
	CountryName string
	Comments    string
}

var loadLocationOnce sync.Once

func GetTimeZones() []*Zone {

	loadLocationOnce.Do(func() {
		for _, tz := range zones {
			var err error
			tz.Location, err = time.LoadLocation(tz.Zone)
			if err != nil {
				slog.Error(fmt.Sprintf("unable to load time zone location from the OS: %+v", err))
				os.Exit(1)
			}
		}

		sort.Slice(zones, func(i, j int) bool {
			return zones[i].CountryName < zones[j].CountryName
		})
	})
	return zones
}

func GetTimeZoneByZoneInfo(zoneInfo string) *Zone {
	zones := GetTimeZones()

	for _, tz := range zones {
		if tz.Zone == zoneInfo {
			return tz
		}
	}

	return nil
}

